import axios from "axios";

const baseURL = "http://localhost:21309/api/";

//כל הפונקציות עובדות ב"ה

//הוספת מוזמן
//מקבלת מופע של מוזמן
export default function AddNewProduct(product) {
  debugger;
  axios
    .get(`${baseURL}Category/GetIdSubCategory/${product.IdSubcategory}`)
    .then(function (response) {
      product.IdSubCategory = response.data;

      axios
        .post(`${baseURL}Product/AddProducts`, product)

        .then(function (response) {
          // handle success
          console.log(response);
        })
        .catch(function (error) {
          // handle error
          console.log(error);
        })
        .then(function () {
          // always executed
        });
    })

    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    });
}

// //// החזרה של כל המוזמנים לפי קוד אירוע
