import Password from "antd/lib/input/Password";

class Loginup extends React.Component {
  state = {
    FirstNameUser: null,
    LastNameUser: "",
    UserPassword: "",
    UserwPassword: "",
    GetPostMail: "",
    EMail: null,
    IdUser: "",
    redirect: false,
