import axios from "axios";
import React, { useState } from "react";
import Products from "../components/Product";
import { useHistory } from "react-router-dom";
import DepleteState from "../components/addProduct";
//יש בעיה שאי אפשר לממש כאן את
// Use History כי זה שייך לספרייה של ריאקט ואין כאן קומפוננה

const baseURL = "http://localhost:21309/api/";
const string = "תאומים";

export const GetAllProducts = () =>
  axios
    .get(`${baseURL}Product/GetAllProducts/4`)
    .then((res) => {
      // console.log(res);
      // history.push("/");
      //history.push("/Products");
            window.location.reload();
      console.log("then");
      // console.log(res.data[0].NameProduct);
      return res.data;
    })
    .catch(function (error) {
      console.log("catch");
      console.log(error);
    });

export const GetProductsByStr = (str) => {
  return axios
    .get(`${baseURL}Product/GetProductsByStr/${str}`)
    .then((res) => {
      console.log("then");
      // console.log(res.data[0].NameProduct);
      return res.data;
    })
    .catch(function (error) {
      console.log("catch");
      console.log(error);
    });
};

export const AddNewProduct = (product, images) => {
  debugger;
  //מביאה רשימה של תת קטגוריות
  axios
    .get(`${baseURL}SubCategory/GetIdSubCategory/${product.IdSubcategory}`)
    // .get(`${baseURL}Category/GetIdSubCategory/${product.IdSubcategory}`)
    .then(function (response) {
      product.IdSubCategory = response.data;

      //מוסיפה מוצר לאתר
      axios
        .post(`${baseURL}Product/AddProducts`, product)

        .then(function (response) {
          const len = images["length"];
          images[len] = response.data;

          //הוספת תמונות

          axios
            .post(`${baseURL}ProductImage/Addimages`, images)
            .then(function (response) {
              console.log(response);
              DepleteState();
            })
            .catch(function (error) {
              console.log(error);
            })

            .then(function () {});
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        })
        .then(function () {});
    })

    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    });
};

export const GetAllCategories = () => {
  debugger;
  return axios
    .get(`http://localhost:21309/api/Category/GetAllCategories`)
    .then((res) => {
      console.log("then");
      console.log(res.data);
      return res.data;
    })
    .catch(function (error) {
      console.log("catch");
      return error;
    });
};

export const GetSearchProducts = (val) => {
  debugger;
  return (
    axios
      .get(`http://localhost:21309/api/Products/GetSearchProducts/${val}`)
      // .get(`${baseURL}Product/GetSearchProducts/${string}`)
      .then((res) => {
        console.log("then");
        console.log(res);
        return res.data;
      })
      .catch(function (error) {
        console.log("catch");
        return error;
      })
  );
};

export const GetAllProductsWithoutSearch = () => {
  debugger;
  return axios
    .get(`http://localhost:21309/api/Product/GetAllProductsWithoutSearch`)
    .then((res) => {
      return res.data;
    })
    .catch(function (error) {
      console.log("catch");
      return error;
    });
};
