    .get("Category/GetSubCategories/" + selectedCategory)
    .then((result) => {
      setMyArray2(result.data);
      console.log(result.data);
    });
    
    axios
    .get("ShoppingCart/Login/"+user.EMail)
    .then( (response)=> {
      user.idUser = response.data;
    })
    .catch(function (error) {
