import React, { Component } from "react";
import Login from "./Login";
import Main from "./Main";
import "../css/login.css";

import axios from "../axios";
import { render } from "@testing-library/react";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

const { useState } = React;

class Loginup extends React.Component {
  state = {
    FirstNameUser: "",
    LastNameUser: "",
    UserPassword: "",
    GetPostMail: "",
    EMail: "",
  };

  newUserHadler = (e) => {
    e.preventDefault();

    axios
      .post("Users/Register", this.state, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((x) => {
        console.log("sucses!" + x);
      });
  };

  checkGet = (e) => {
    axios.get("user/CheckGet", this.state).then((x) => {
      console.log("sucses!" + x);
    });
  };

  changeFirstName = (e) => {
    this.setState({ FirstNameUser: e.target.value });
  };

  changeLastName = (e) => {
    this.setState({ LastNameUser: e.target.value });
  };

  changeEmail = (e) => {
    this.setState({ EMail: e.target.value });
  };

  changepassword = (e) => {
    this.setState({ password: e.target.value });
  };

  changeGetMail = (e) => {
    this.setState({ GetPostMail: e.target.value });
  };

  render() {
    return (
      <div className="loginUp">
        <div className="Login">
          <form onSubmit={this.newUserHadler}>
            <label htmlFor="Firstname">שם פרטי</label>
            <input
              type="text"
              id="Firstname"
              className="input"
              value={this.state.FirstName}
              onChange={this.changeFirstName}
            />

            <label htmlFor="Lastname">שם משפחה</label>
            <input
              type="text"
              id="Lastname"
              className="input"
              value={this.state.lastName}
              onChange={this.changeLastName}
            />

            <label htmlFor="email">כתובת מייל</label>
            <input
              type="mail"
              id="email"
              className="input"
              value={this.state.email}
              onChange={this.changeEmail}
            />

            <label htmlFor="password">סיסמה</label>
            <input
              type="password"
              id="password"
              className="input"
              value={this.state.email}
              onChange={this.changepassword}
            />

            <label htmlFor="Wpassword">אימות סיסמה</label>
            <input type="password" id="Wpassword" className="input" />

            <label htmlFor="GetPostMail">סיסמה</label>
            <input
              type="checkbox"
              id="GetPostMail"
              className="input"
              value={this.state.GetPostMail}
              onChange={this.changeGetMail}
            />
            <button type="submit" className="input SubmiSignup">
              הרשמה
            </button>
          </form>
        </div>



        <div className="signup">
          <form>
            <label htmlFor="mail">כתובת מייל</label>
            <input type="mail" id="mail" className="input" />

            <label htmlFor="account">סיסמה</label>
            <input type="password" className="input" id="account" />
            <button
              type="submit"
              className="input SubmiSignup"
              onClick={() => this.newUserHadler(this.state.name)}
            >
              כניסה
            </button>
          </form>
        </div>
        <button onClick={this.checkGet}>click</button>
      </div>
    );
  }
}
