import ReactDOM from "react-dom";
import React, { useState } from "react";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

import "../css/ManagerForms.css";

export default function ManagerForms(props) {
  const [color, setColor] = useState("pink");

  let colorStyle = {
    backgroundColor: { color },
  };

  function ChangeColor() {
    if (color == "pink") {
      setColor("green");
    } else {
      setColor("pink");
    }
  }

  return (
    <div>
      <table className="TablebtnForm">
        <tr>
          <button className="manuButton" tupe="submit" value="דף הבית"></button>
          <button className="manuButton" tupe="submit" value="חנות"></button>
          <button className="manuButton" type="submit">
            סניפים
          </button>
        </tr>
        <tr>
          <ul>
            <li>
              <Link to="/shoppingCart">נעליים</Link>
            </li>
            <li>
              <Link to="/shoppingCart">בגדי בנים</Link>
            </li>
            <li>
              <Link to="/shoppingCart">בגדי בנות</Link>
            </li>
          </ul>
        </tr>
      </table>
      <div style={colorStyle.backgroundColor} className="main">
        <button className="ChangeColor" onClick={() => ChangeColor()}>
          ChangeColor
        </button>
      </div>
      <div className="ManagerForms">
        <form className="addForm">
          <label>שם מוצר</label>
          <input type="text" className="input" />
          <label>קטגוריה</label>
          <input type="text" className="input" />
          <label>תת קטגוריה</label>
          <input type="text" className="input" />
          <label>מחיר</label>
          <input type="money" className="input" />
        </form>
      </div>
    </div>
  );
}
