import React from "react";
import "../css/login.css";
import "../css/round.css";

import axios from "../axios";

class Loginup extends React.Component {
  state = {
    FirstNameUser: "",
    LastNameUser: "",
    UserPassword: "",
    UserwPassword: "",
    GetPostMail: "",
    EMail: "",
  };

  newUserHadler = (e) => {
    e.preventDefault();

    axios
      .post("Users/Register", this.state, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((x) => {
        console.log("sucses!" + x);
      });
  };

  
  state = {
    password: '',
    confirmPassword: ''
}
  

  checkPassword = () => {
    
    const { password, confirmPassword } = this.state;
    // perform all neccassary validations
    if (password !== confirmPassword) {
        alert("Passwords don't match");
    } else {
        // make API call
    }
  }



  checkGet = (e) => {
    axios.get("user/CheckGet", this.state).then((x) => {
      console.log("sucses!" + x);
    });
  };

  changeFirstName = (e) => {
    this.setState({ FirstNameUser: e.target.value });
  };

  changeLastName = (e) => {
    this.setState({ LastNameUser: e.target.value });
  };

  changeEmail = (e) => {
    this.setState({ EMail: e.target.value });
  };

  changepassword = (e) => {
    this.setState({ password: e.target.value });
  };

  changeGetMail = (e) => {
    this.setState({ GetPostMail: e.target.value });
  };
  render() {
    return (
      <div className="loginUp">
        {/*LOG-IN*/}
        <div className="Login">
          <form onSubmit={this.newUserHadler}>
            <label htmlFor="Firstname">שם פרטי</label>
            <input
              type="text"
              id="Firstname"
              placeholde="שם פרטי"
              className="input"
              value={this.state.FirstName}
              onChange={this.changeFirstName}
            />

            <label htmlFor="Lastname">שם משפחה</label>
            <input
              type="text"
              id="Lastname"
              className="input"
              value={this.state.lastName}
              onChange={this.changeLastName}
            />

            <label htmlFor="email">כתובת מייל</label>
            <input
              type="mail"
              id="email"
              className="input"
              value={this.state.email}
              onChange={this.changeEmail}
            />

            <label htmlFor="password">סיסמה</label>
            <input
              type="password"
              id="password"
              className="input"
              value={this.password}
              onChange={this.changepassword}
            />

            <label htmlFor="Wpassword">אימות סיסמה</label>
            <input
              type="password"
              id="Wpassword"
              className="input"
              value={this.confirmPassword}
              onChange={this.checkPassword}
            />

            <div class="round">
              <input
                type="checkbox"
                id="GetPostMail"
                value={this.state.GetPostMail}
                onChange={this.changeGetMail}
              />
              <label htmlFor="GetPostMail"></label>
            </div>
            {/* 
            <label>סיסמה</label>
            <input type="checkbox" className="input" /> */}
            <button type="submit" className="input SubmiSignup">
              הרשמה
            </button>
          </form>
        </div>

        {/*SIGN-UP*/}
        <div className="signup">
          <form>
            <label htmlFor="mail">כתובת מייל</label>
            <input type="mail" id="mail" className="input" />

            <label htmlFor="account">סיסמה</label>
            <input type="password" className="input" id="account" />
            <button
              type="submit"
              className="input SubmiSignup"
              onClick={() => this.newUserHadler(this.state.name)}
            >
              כניסה
            </button>
          </form>
        </div>
        <button onClick={this.checkGet}>click</button>
      </div>
    );
  }
}

export default Loginup;

