import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "../css/shoppingcart.css";

function ShoppingCart(props) {
  const location = useLocation();
  const [arr, setarr] = useState([]);
  const [arrayofbuy, setarray] = useState([]);
  const [ProductsCart, setProductsCart] = useState(
    JSON.parse(localStorage.getItem("CartProduct"))
  );

  var AlertCode = "<div> סל הקניות שלך ריק  </div>";

  useEffect(() => {
    setProductsCart(JSON.parse(localStorage.getItem("CartProduct")));
    // ProductsCart = JSON.parse(localStorage.getItem("CartProduct"));
    ProductsCart.push(JSON.parse(localStorage.getItem("CartProduct")));

    if (ProductsCart.length > 0)
      document.getElementById("h2Head").innerHTML = FullList();
    else document.getElementById("h2Head").innerHTML = NullList();
  }, []);

