import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import ReactDOM from "react-dom";

import { withRouter } from "react-router-dom";
import "../css/shoppingcart.css";

console.clear();

let productList = JSON.parse(localStorage.getItem("CartProduct"));

/* Product */

/* Total */

/* ProductForm */

/* ProductList */
// class Body extends React.component {
//   constructor(props) {
//     super(props);
//   }

//   render() {
//     return <div id="body"> </div>;
//   }
// }
// export default withRouter(Body);
class shoppingCart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      total: 0,
      productList: "",
    };

    this.createProduct = this.createProduct.bind(this);
    this.calculateTotal = this.calculateTotal.bind(this);
    this.showProduct = this.showProduct.bind(this);
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({ productList: productList });
    }, 1000);
  }

  createProduct(product) {
    this.setState({
      products: this.state.productList.push(product),
    });
  }

  calculateTotal(price) {
    this.setState({
      total: this.state.total + price,
    });
    console.log(this.state.total);
  }

  showProduct(info) {
    console.log(info);
    alert(info);
  }

  render() {
    if (!this.state.productList) return <h5>loading...</h5>;

    var component = this;
    var products = this.state.productList.map(function (product) {
      return (
        <Product
          name={product.NameProduct}
          price={product.Price}
          img={product.Images[0]}
          info={product.DescriptionProduct}
          handleShow={component.showProduct}
          handleTotal={component.calculateTotal}
        />
      );
    });

    return <div>{products}</div>;
  }
}
export default withRouter(shoppingCart);

class Product extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      qty: 0,
    };
    this.add = this.add.bind(this);
    this.subtract = this.subtract.bind(this);
    this.showInfo = this.showInfo.bind(this);
  }

  add() {
    this.setState({
      qty: this.state.qty + 1,
    });
    this.props.handleTotal(this.props.price);
  }

  subtract() {
    this.setState({
      qty: this.state.qty - 1,
    });
    this.props.handleTotal(-this.props.price);
  }

  showInfo() {
    this.props.handleShow(this.props.info);
  }

  render() {
    return (
      <div>
        <div className="row form-group">
          <img src={this.props.img} alt="..."></img>
          <div className="col-sm-10">
            <h4>
              {this.props.name}: {this.props.price} ₪
            </h4>
          </div>
          <div className="col-sm-2 text-right"> {this.state.qty} :כמות</div>
        </div>
        <div className="row btn-toolbar">
          <div className="col-6">
            <button className="btn btn-outline-primary" onClick={this.showInfo}>
              ראה פירוט מוצר
            </button>
          </div>
          <div className="col-6 text-right">
            <button className="btn btn-outline-primary" onClick={this.add}>
              +1
            </button>
            <button
              className="btn btn-outline-primary"
              onClick={this.subtract}
              disabled={this.state.qty < 1}
            >
              -1
            </button>
          </div>
        </div>
        <hr />
      </div>
    );
  }
}
