import ReactDOM from "react-dom";
import React, { useState } from "react";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

import "../css/ManagerForms.css";

export default function ManagerForms(props) {
  const [color, setColor] = useState("pink");

  let colorStyle = {
    backgroundColor: { color },
  };

  function ChangeColor() {
    if (color == "pink") {
      setColor("green");
    } else {
      setColor("pink");
    }
  }

  return (
    <div>
      <div style={colorStyle.backgroundColor} className="main">
        <button className="ChangeColor" onClick={() => ChangeColor()}>
          ChangeColor
        </button>
      </div>
      <Router>
        <div>
          <Menu />
          <Switch>
            <Route path="/ManagerForms/AddProduct">
              <AddProduct />
            </Route>
            <Route path="/ManagerForms/DeleteProduct">
              <DeleteProduct />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}

function Menu() {
  return (
    <ul className="ListbtnForm">
      <li>
        <Link to="/AddProduct">הוספת מוצר</Link>
      </li>
      <li>
        <Link to="/DeleteProduct">מחיקת מוצר</Link>
      </li>
      <li>
        <Link to="/shoppingCart">נתינת הרשאה</Link>
      </li>
      <li>
        <Link to="/shoppingCart">מחיקת הרשאה</Link>
      </li>
    </ul>
  );
}

/* Add Product Form */
function AddProduct() {
  return (
    <div className="AddProductDiv ManagerForms">
      <form className="AddProductForm FormContent">
        <label>שם מוצר</label>
        <input type="text" className="input" />
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>מחיר</label>
        <input type="money" className="input" />
      </form>
    </div>
  );
}

/* Delete Product Form */
function DeleteProduct() {
  return (
    <div className="DeleteProductDiv ManagerForms">
      <form className="DeleteProductForm FormContent">
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>שם מוצר</label>
        <input type="text" className="input" />
      </form>
    </div>
  );
}
