import axios from "axios";
// import DepleteImages from "../components/addProduct";

const baseURL = "http://localhost:21309/api/";

//כל הפונקציות עובדות ב"ה

export const AddNewProduc = (product, images) => {
  debugger;
  //מביאה רשימה של תתקטגוריות
  axios
    .get(`${baseURL}Category/GetIdSubCategory/${product.IdSubcategory}`)
    .then(function (response) {
      product.IdSubCategory = response.data;

      //מוסיפה מוצר לאתר
      axios
        .post(`${baseURL}Product/AddProducts`, product)

        .then(function (response) {
          const len = images["length"];
          images[len] = response.data;

          //הוספת תמונות

          axios
            .post(`${baseURL}ProductImage/Addimages`, images)
            .then(function (response) {
              console.log(response);
              // DepleteImages();
            })
            .catch(function (error) {
              console.log(error);
            })

            .then(function () {});
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        })
        .then(function () {});
    })

    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    });
};

export const GetAllProducts = () => {
  debugger;
  axios
    .get(`${baseURL}Product/GetAllProducts/8`)
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
};
