import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import axios from "../axios";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";
import "../css/ManagerForms.css";

import { DatePicker } from "antd";
//import { useForm } from "react-hook-form";

/* Add Product Form */
export default function AddProduct(/*{ onSubmit }*/) {
  // const UserForm = (props) => {
  const [Product, setProduct] = useState(props.Product);

  const submit = (e) => {
    debugger;
    // e.preventDefault();
    // fetch("/api", {
    //   method: "POST",
    //   body: JSON.stringify({ Product }),
    //   headers: { "Content-Type": "application/json" },
    // })
    //   .then((res) => res.json())
    //   .then((json) => setProduct(json.Product));
  };

  const [NameProduct, setNameProduct] = useState("");
  const [Color, setColor] = useState("");
  const [size, setsize] = useState("");
  const [IdSubcategory, setIdSubcategory] = useState("");
  const [Company, setCompany] = useState("");
  const [UnitsInStock, setUnitsInStock] = useState("");
  const [Price, setPrice] = useState("");
  const [ImageOfProduct, setImageOfProduct] = useState("");
  const [DescriptionProduct, setDescriptionProduct] = useState("");

  // const SelectNameProduct = (e) => {
  //   setNameProduct(e.target.value);
  //   setColor(e.target.value);
  //   setPrice(e.target.value);
  // };
  /*onLoad*/
  const useMountEffect = (fun) => useEffect(fun, []);

  useMountEffect(() => {
    GetAllCategories();
  });

  /* on submit*/
  // const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  /* list Category*/
  const [myArray, setMyArray] = useState([]);

  /* list subCategory*/
  const [myArray2, setMyArray2] = useState([]);

  const [changeCategory, setCategory] = useState();
  const SelectCategory = (e) => {
    setCategory(e.target.value);
    GetSubCategories(e.target.value);
  };

  // debugger;
  // // const [color, setColor] = useState("");
  // //const [password, setPassword] = useState("")

  // const handleSubmit = (e) => {
  //   debugger;
  //   e.preventDefault();
  //   onSubmit(SelectNameProduct(e.target.value));
  // };

  // onChange = e => {
  //   this.setState({ company: e.target.value });
  // };

  function GetAllData() {
    console.log(Color, NameProduct);
  }
  function GetAllCategories() {
    axios.get("Category/GetAllCategories").then((result) => {
      setMyArray(result.data);
      console.log(result.data);
    });
  }

  function GetSubCategories(selectedCategory) {
    debugger;
    axios
      .get("Category/GetSubCategories/" + selectedCategory)
      .then((result) => {
        setMyArray2(result.data);
        console.log(result.data);
      });
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <form className="AddProductForm FormContent" onSubmit={submit}>
        <div className="row">
          <div className="col">
            <input
              type="text"
              className="form-control input"
              autoFocus
              placeholder="שם מוצר"
              //onChange={setNameProduct}
              value={Product.NameProduct}
              onChange={(e) =>
                setNameProduct({ ...Product, NameProduct: e.target.value })
              }
            ></input>
          </div>

          <div className="col">
            <input
              type="color"
              className="form-control input"
              placeholder="צבע"
              //  value={Color}
              //onChange={setColor}
              value={Product.Color}
              onChange={(e) => setColor({ ...Product, Color: e.target.value })}
            ></input>
          </div>

          <div className="col">
            <select
              type="select"
              className="form-control input"
              options={myArray}
              onChange={setCategory}
            >
              <option value="" disabled selected>
                בחר קטגוריה
              </option>
              {myArray.map((value) => (
                <option value={value}>{value}</option>
              ))}
            </select>
          </div>
          <div className="col">
            <input
              type="text"
              className="form-control input"
              placeholder="מידה"
              //value={value}
              onChange={setsize}
            ></input>
          </div>
          <div className="col">
            <select
              type="select"
              className="form-control input"
              options={myArray2}
            >
              <option value="" disabled selected>
                בחר תת קטגוריה
              </option>
              {myArray2.map((value) => (
                <option>{value}</option>
              ))}
            </select>
          </div>
          <div className="col">
            <input
              type="text"
              className="form-control input"
              placeholder="חברה"
            ></input>
          </div>

          <div className="col">
            <input
              type="number"
              className="form-control input"
              placeholder="כמות"
              //    onChange={SelectNameProduct}
            ></input>
          </div>

          <div className="col">
            <input
              type="money"
              className="form-control input"
              placeholder="מחיר"
            ></input>
          </div>
          <div className="col">
            <input
              type="file"
              className="form-control input"
              placeholder="הכנס תמונה"
              name="picture"
            />
          </div>
          <div className="col">
            <textarea
              type="text"
              className="form-control input form-control-file"
              placeholder="תיאור מוצר"
              rows="3"
              cols="34"
            ></textarea>
          </div>
        </div>
        <input
          type="submit"
          className="btn btn-primary mt-3"
          value="submit"
          // onClick={GetAllData()}
        ></input>
        {/* 
        <input
          type="submit"
          className=""
        ></input> */}
        {/* <article>
        <label>שם מוצר</label>
        <input type="text" className="form-control input" autoFocus />
        <label>קטגוריה</label>
        <br />
        <select type="select" options={myArray} onChange={SelectCategory}>
          {myArray.map((value) => (
            <option>{value}</option>
          ))}
        </select>
        <br />
        <label>תת קטגוריה</label>
        <br />
        <select type="select" options={myArray2}>
          {myArray2.map((value) => (
            <option>{value}</option>
          ))}
        </select>
        <br />
        <label>צבע</label>
        <input type="color" className="input" />
        <label>מידה</label>
        <input type="text" className="input" />

        <label>תיאור מוצר</label>
        <textarea
          rows="3"
          cols="34" /* value={this.state.value} onChange={(e) => this.setState({value: e.target.value})}
        ></textarea>
      </article>
      <article>
        <div label="DatePicker">
          <DatePicker />
        </div>
        <label>חברה</label>
        <input type="text" className="input" />
        <label>כמות</label>
        <input type="number" className="input" />
        <label>מחיר</label>
        <input type="money" className="input" />
        <label>תמונה</label>
        <input ref={register} type="file" name="picture" />
      </article> */}
      </form>
    </div>
  );
}
