import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import axios from "../axios";
import { BrowserRouter as router } from "react-router-dom";
import AddNewProduct from "../FUNCTION/ProductFunction";
import GetIdSubCategory from "../FUNCTION/ProductFunction";
import Image from "./image";
import CheckableList from "./image";
import "../css/ManagerForms.css";

import { DatePicker } from "antd";
//import { useForm } from "react-hook-form";

/* Add Product Form */
export default function AddProduct(/*{ onSubmit }*/) {
  // const UserForm = (props) => {
  // const [Product, setProduct] = useState(props.Product);

  // const submit = (e) => {
  //   debugger;
  // e.preventDefault();
  // fetch("/api", {
  //   method: "POST",
  //   body: JSON.stringify({ Product }),
  //   headers: { "Content-Type": "application/json" },
  // })
  //   .then((res) => res.json())
  //   .then((json) => setProduct(json.Product));
  //}

  const [NameProduct, setNameProduct] = useState("");
  const [Color, setColor] = useState("");
  const [size, setsize] = useState("");
  const [IdSubcategory, setIdSubcategory] = useState("");
  const [Company, setCompany] = useState("");
  const [UnitsInStock, setUnitsInStock] = useState("");
  const [Price, setPrice] = useState("");
  const [ImageOfProduct, setImageOfProduct] = useState("");
  const [DescriptionProduct, setDescriptionProduct] = useState("");

  function changeNameProduct(val) {
    setNameProduct(val);
  }

  function changeColor(val) {
    setColor(val);
  }

  function changeSize(val) {
    setsize(val);
  }

  function changeIdSubcategory(val) {
    setIdSubcategory(val);
  }

  function changeCompany(val) {
    setCompany(val);
  }

  function changeUnitsInStock(val) {
    setUnitsInStock(val);
  }

  function changePrice(val) {
    setPrice(val);
  }

  function changeImageOfProduct(val) {
    setImageOfProduct(val);
  }

  function changeDescriptionProduct(val) {
    setDescriptionProduct(val);
  }

  //מונע מהדף להתרפרש מליון פעם
  const handleSubmit = (event) => {
    debugger;
    console.log(event);
    // setIdSubcategory(GetIdSubCategory({ IdSubcategory }));

    AddNewProduct({
      NameProduct: NameProduct,
      Color: Color,
      size: size,
      IdSubcategory: IdSubcategory,
    });
    event.preventDefault();
  };

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   AddNewProduct({ NameProduct: NameProduct, Color: Color, size: size });
  // };
  // const SelectNameProduct = (e) => {
  //   setNameProduct(e.target.value);
  //   setColor(e.target.value);
  //   setPrice(e.target.value);
  // };
  /*onLoad*/
  const useMountEffect = (fun) => useEffect(fun, []);

  useMountEffect(() => {
    GetAllCategories();
  });

  /* on submit*/
  // const { register, handleSubmit } = useForm();

  // const onSubmit = (data) => {
  //   console.log(data);
  // };

  /* list Category*/
  const [myArray, setMyArray] = useState([]);

  /* list subCategory*/
  const [myArray2, setMyArray2] = useState([]);

  const [changeCategory, setCategory] = useState();
  const SelectCategory = (e) => {
    setCategory(e.target.value);
    GetSubCategories(e.target.value);
  };

  // debugger;
  // // const [color, setColor] = useState("");
  // //const [password, setPassword] = useState("")

  // const handleSubmit = (e) => {
  //   debugger;
  //   e.preventDefault();
  //   onSubmit(SelectNameProduct(e.target.value));
  // };

  // onChange = e => {
  //   this.setState({ company: e.target.value });
  // };

  function GetAllCategories() {
    axios.get("Category/GetAllCategories").then((result) => {
      setMyArray(result.data);
      console.log(result.data);
    });
  }

  function GetSubCategories(selectedCategory) {
    debugger;
    axios
      .get("Category/GetSubCategories/" + selectedCategory)
      .then((result) => {
        setMyArray2(result.data);
        console.log(result.data);
      });
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <div className="backgroundGradient">
        <form className="AddProductForm FormContent" onSubmit={handleSubmit}>
          <div className="row">
            <div className="col">
              <input
                type="text"
                className="form-control input"
                autoFocus
                placeholder=" "
                //onChange={setNameProduct}
                value={NameProduct}
                onChange={(e) => changeNameProduct(e.target.value)}
              ></input>
              <label>שם מוצר</label>
            </div>
            <div className="col">
              <select
                type="select"
                className="form-control input"
                placeholder=" "
                value={Color}
                onChange={(e) => changeColor(e.target.value)}
              >
                <option value="" disabled selected>
                  בחר צבע
                </option>
                <option>אדום</option>
                <option>ורוד</option>
                <option>כחול</option>
                <option>תכלת</option>
                <option>אפור</option>
                <option>לבן</option>
                <option>שחור</option>
                <option>ירוק</option>
                <option>כתום</option>
                <label>שם מוצר</label>
              </select>
              {/* <label>צבע</label> */}
            </div>
            <div className="col">
              <select
                type="select"
                className="form-control input"
                options={myArray}
                onChange={(e) => GetSubCategories(e.target.value)}
              >
                <option value="" disabled selected>
                  בחר קטגוריה
                </option>
                {myArray.map((value) => (
                  <option value={value}>{value}</option>
                ))}
              </select>
            </div>
            <div className="col">
              <input
                type="text"
                className="form-control input"
                placeholder=" "
                value={size}
                onChange={(e) => changeSize(e.target.value)}
              ></input>
              <label>מידה</label>
            </div>
            <div className="col">
              <select
                type="select"
                className="form-control input"
                options={myArray2}
                value={IdSubcategory}
                onChange={(e) => changeIdSubcategory(e.target.value)}
              >
                <option value="" disabled selected>
                  בחר תת קטגוריה
                </option>
                {myArray2.map((value) => (
                  <option>{value}</option>
                ))}
              </select>
            </div>
            <div className="col">
              <input
                type="text"
                className="form-control input"
                placeholder=" "
                value={Company}
                onChange={(e) => changeCompany(e.target.value)}
              ></input>
              <label>חברה</label>
            </div>
            <div className="col">
              <input
                type="number"
                className="form-control input"
                placeholder=" "
                value={UnitsInStock}
                min="1"
                onChange={(e) => changeUnitsInStock(e.target.value)}
                //    onChange={SelectNameProduct}
              ></input>
              <label>כמות</label>
            </div>
            <div className="col">
              <input
                type="money"
                className="form-control input"
                placeholder=" "
                value={Price}
                onChange={(e) => changePrice(e.target.value)}
              ></input>
              <label>מחיר ₪</label>
            </div>
            {/* <Image /> */}{" "}
            <div className="col">
              <input
                type="file"
                className="form-control input"
                placeholder="הכנס תמונה"
                //name="picture"
                value={ImageOfProduct}
