import React from "react";
import "../css/login.css";
import "../css/round.css";

import axios from "../axios";
import GetIdUser from "../FUNCTION/LoginFunction";

class Loginup extends React.Component {
  state = {
    FirstNameUser: "",
    LastNameUser: "",
    UserPassword: "",
    UserwPassword: "",
    GetPostMail: "",
    EMail: "",
    IdUser: "",
  };

  newUserHadler = () => {
    axios
      .post("Users/Register", this.state, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((x) => {
        console.log("sucses!" + x);
      });
  };

  state = { IdUser: " " };

  login = () => {
    debugger;
    //axios... return id
    async () => {
      await this.setState({
        IdUser: GetIdUser({
          EMail: this.state.EMail,
          UserPassword: this.state.UserPassword,
        }),
      });
    };
    localStorage.setItem("IdUser", this.state.IdUser);
  };

  checkPassword = () => {
    const { UserPassword, UserwPassword } = this.state;
    // perform all neccassary validations
    if (UserPassword !== UserwPassword) {
      alert("Passwords don't match");
    } else {
      alert("good password"); // make API call
      this.newUserHadler();
    }
  };

  checkGet = (e) => {
    axios.get("user/CheckGet", this.state).then((x) => {
      console.log("sucses!" + x);
    });
  };

  changeFirstName = (e) => {
    this.setState({ FirstNameUser: e.target.value });
  };

  changeLastName = (e) => {
    this.setState({ LastNameUser: e.target.value });
  };

  changeEmail = (e) => {
    this.setState({ EMail: e.target.value });
  };

  changepassword = (e) => {
    this.setState({ UserPassword: e.target.value });
  };

  changeWpassword = (e) => {
    this.setState({ UserwPassword: e.target.value });
  };

  changeGetMail = (e) => {
    this.setState({ GetPostMail: e.target.value });
  };

  render() {
    return (
      <div className="loginUp">
        {/*LOG-IN*/}
        <div className="Login">
          <form onSubmit={this.checkPassword}>
            <article>
              <input
                type="text"
                id="Firstname"
                placeholde="שם פרטי"
                className="input"
                value={this.state.FirstName}
                onChange={this.changeFirstName}
              />
              <label htmlFor="Firstname">שם פרטי</label>

              <input
                type="text"
                id="Lastname"
                className="input"
                value={this.state.lastName}
                onChange={this.changeLastName}
              />
              <label htmlFor="Lastname">שם משפחה</label>

              <label htmlFor="email">כתובת מייל</label>
              <input
                type="mail"
                id="email"
                className="input"
                value={this.state.email}
                onChange={this.changeEmail}
              />

              <label htmlFor="password">סיסמה</label>
              <input
                type="password"
                id="password"
                className="input"
                value={this.state.UserPassword}
                onChange={this.changepassword}
              />

              <label htmlFor="Wpassword">אימות סיסמה</label>
              <input
                type="password"
                id="Wpassword"
                className="input"
                value={this.state.UserwPassword}
                onChange={this.changeWpassword}
              />

              <div className="round">
                <input
                  type="checkbox"
                  id="GetPostMail"
                  value={this.state.GetPostMail}
                  onChange={this.changeGetMail}
                />
                <label htmlFor="GetPostMail"></label>
              </div>
              {/* 
            <label>סיסמה</label>
            <input type="checkbox" className="input" /> */}
              <button type="submit" className="input SubmiSignup">
                הרשמה
              </button>
            </article>
          </form>
        </div>

        {/*SIGN-UP*/}
        <div className="signup">
          {/** <form className="FormContent">
            {/* <article>
              <div className="row">
                <div className="col"> 
            <input
              type="mail"
              className="form-control input"
              id="email"
              placeholder="כתובת מייל"
              name="email"
            ></input>
             </div> 
            <div className="col">
              <input
                type="password"
                className="form-control input"
                id="account"
                placeholder="סיסמה"
                name="pswd"
              ></input>
            </div>
            </div> 
            <button
              type="submit"
              className="input SubmiSignup btn btn-primary mt-3"
              onClick={() => this.newUserHadler(this.state.name)}
            >
              כניסה
            </button>
            <h1>{this.state.IdUser}</h1>
            <button>click</button>

             </article> 
          </form> */}
          <form>
            <input
              type="text"
              className="input"
              id="email"
              placeholder="Enter email"
              onChange={this.changeEmail}
            ></input>
            <input
              type="password"
              className="input"
              id="password"
              placeholder="Enter password"
              onChange={this.changepassword}
            ></input>
            <button
              type="submit"
              className="input SubmiSignup btn btn-primary mt-3"
              onClick={() => this.login()}
            >
              כניסה
            </button>
          </form>
        </div>
      </div>
    );
  }
}
export default Loginup;

