import axios from "axios";

const baseURL = "http://localhost:21309/api/";

//כל הפונקציות עובדות ב"ה

export default function AddNewProduct(product) {
  debugger;
  //מביאה רשימה של תתקטגוריות
  axios
    .get(`${baseURL}Category/GetIdSubCategory/${product.IdSubcategory}`)
    .then(function (response) {
      product.IdSubCategory = response.data;

      //מוסיפה מוצר לאתר
      axios
        .post(`${baseURL}Product/AddProducts`, product)

        .then(function (response) {
          // handle success
          console.log(response);
        })
        .catch(function (error) {
          // handle error
          console.log(error);
        })
        .then(function () {
          // always executed
        });
    })

    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    });
}

