import React, { useState, useEffect } from "react";
import axios from "../axios";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";
import "../css/ManagerForms.css";
import { DatePicker } from "antd";
import { useForm } from "react-hook-form";

/* Add Product Form */
export default function AddProduct() {
  /*onLoad*/
  const useMountEffect = (fun) => useEffect(fun, []);

  useMountEffect(() => {
    GetAllCategories();
  });

  /* on submit*/
  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  /* list Category*/
  const [myArray, setMyArray] = useState([]);

  /* list subCategory*/
  const [myArray2, setMyArray2] = useState([]);

  const [changeCategory, setCategory] = useState();
  const SelectCategory = (e) => {
    setCategory(e.target.value);
    GetSubCategories(e.target.value);
  };

  function GetAllCategories() {
    axios.get("Category/GetAllCategories").then((result) => {
      setMyArray(result.data);
      console.log(result.data);
    });
  }

  function GetSubCategories(selectedCategory) {
    debugger;

    axios
      .get("Category/GetSubCategories/" + selectedCategory)
      .then((result) => {
        setMyArray2(result.data);
        console.log(result.data);
      });
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <form
        className="AddProductForm FormContent"
        onSubmit={handleSubmit(onSubmit)}
      >
        <div className="row">
          <div className="col">
            <input
              type="text"
              className="form-control input"
              autoFocus
              placeholder="שם מוצר"
              name="email"
            ></input>
          </div>

          <div className="col">
            <input
              type="color"
              className="form-control input"
              placeholder="צבע"
            ></input>
          </div>
          <div className="col">
            <select
              type="select"
              className="form-control input"
              options={myArray}
              onChange={SelectCategory}
            >
              {myArray.map((value) => (
                <option>{value}</option>
              ))}{" "}
            </select>
          </div>
          <div className="col">
            <input
              type="text"
              className="form-control input"
              placeholder="מידה"
            ></input>
          </div>
          <div className="col">
            <select
              type="select"
              className="form-control input"
              // placeholder="בחר תת קטגוריה"
              options={myArray2}
            >
              {myArray2.map((value) => (
                <option>{value}</option>
              ))}
            </select>
          </div>
          <div className="col">
            <input
              type="text"
              className="form-control input"
              placeholder="חברה"
            ></input>
          </div>

          <div className="col">
            <input
              type="number"
              className="form-control input"
              placeholder="כמות"
            ></input>
          </div>

          <div className="col">
            <input
              type="money"
              className="form-control input"
              placeholder="מחיר"
            ></input>
          </div>
          <div className="col">
            <input
              type="file"
              className="form-control input"
              placeholder="הכנס תמונה"
              name="picture"
            />
          </div>
          <div className="col">
            <textarea
              type="text"
              className="form-control input form-control-file"
              placeholder="תיאור מוצר"
              rows="3"
              cols="34"
            ></textarea>
          </div>
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Submit
        </button>
        {/* <article>
          <label>שם מוצר</label>
          <input type="text" className="form-control input" autoFocus />
          <label>קטגוריה</label>
          <br />
          <select type="select" options={myArray} onChange={SelectCategory}>
            {myArray.map((value) => (
              <option>{value}</option>
            ))}
          </select>
          <br />
          <label>תת קטגוריה</label>
          <br />
          <select type="select" options={myArray2}>
            {myArray2.map((value) => (
              <option>{value}</option>
            ))}
          </select>
          <br />
          <label>צבע</label>
          <input type="color" className="input" />
          <label>מידה</label>
          <input type="text" className="input" />

          <label>תיאור מוצר</label>
          <textarea
            rows="3"
            cols="34" /* value={this.state.value} onChange={(e) => this.setState({value: e.target.value})}
          ></textarea>
        </article>
        <article>
          <div label="DatePicker">
            <DatePicker />
          </div>
          <label>חברה</label>
          <input type="text" className="input" />
          <label>כמות</label>
          <input type="number" className="input" />
          <label>מחיר</label>
          <input type="money" className="input" />
          <label>תמונה</label>
          <input ref={register} type="file" name="picture" />
        </article> */}
      </form>
    </div>
  );
}
