import React, { useState } from "react";
import ReactDOM from "react-dom";

import { useForm } from "react-hook-form";
import axios from "../axios";
// import Select from 'react-select';

import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

import "../css/ManagerForms.css";

export default function ManagerForms(props) {
  const [color, setColor] = useState("pink");

  // let colorStyle = {
  //   backgroundColor: { color },
  // };

  const { items } = props;
  const [selectedItems, setSelectedItems] = useState([]);

  function selectItem(e) {
    const checked = e.target.checked;
    const item = e.target.parentElement.textContent;

    if (checked) {
      setSelectedItems((items) => items.concat([item]));
    } else {
      setSelectedItems((items) => items.filter((x) => x !== item));
    }
  }

  function ChangeColor() {
    if (color === "pink") {
      setColor("green");
    } else {
      setColor("pink");
    }
  }

  return (
    <div>
      <div style={colorStyle.backgroundColor} className="main">
        <button className="ChangeColor" onClick={() => ChangeColor()}>
          ChangeColor
        </button>
      </div>
      <Router>
        <div>
          <MenuForms />
          <Switch>
            <Route path="/ManagerForms/AddProduct">
              <AddProduct />
            </Route>
            <Route path="/ManagerForms/DeleteProduct">
              <DeleteProduct />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}

function MenuForms() {
  return (
    <div className="aaa">
      <ul className="ListbtnForm">
        <li>
          <Link to="/ManagerForms/AddProduct" className="ListbtnForm">
            הוספת מוצר
          </Link>
        </li>
        <li>
          <Link to="/ManagerForms/DeleteProduct" className="ListbtnForm">
            מחיקת מוצר
          </Link>
        </li>
        <li>
          <Link to="/shoppingCart" className="ListbtnForm">
            נתינת הרשאה
          </Link>
        </li>
        <li>
          <Link to="/shoppingCart" className="ListbtnForm">
            מחיקת הרשאה
          </Link>
        </li>
      </ul>
    </div>
  );
}

/* Add Product Form */
export function AddProduct() {
  const { register, handleSubmit } = useForm();

  state = {
    list1: [],
  };

  const onSubmit = (data) => {
    console.log(data);
  };

  function GetAllCategories() {
    axios.get("Category/GetAllCategories").then((result) => {
      this.list1 = result.data;
      alert(this.list1);
    });
    // .catch((err) => {
    //   alert(console.error);
    // }
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <form
        className="AddProductForm FormContent"
        onSubmit={handleSubmit(onSubmit)}
        onLoad={GetAllCategories()}
      >
        <label>שם מוצר</label>
        <input type="text" className="input" />
        <label>קטגוריה</label>
        {/* <select id="CuisineList" options={this.state.list1.options}></select> */}
        {/* <Select name="form-field-name" options={this.state.list1.options} /> */}
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>תיאור מוצר</label>
        <textarea
          rows="3"
          cols="34" /* value={this.state.value} onChange={(e) => this.setState({value: e.target.value})}*/
        ></textarea>
        <label>מחיר</label>
        <input type="money" className="input" />
        <input ref={register} type="file" name="picture" />
        <img src={register} alt="img" />
      </form>
    </div>
  );
}

/* Delete Product Form */
export function DeleteProduct() {
  return (
    <div className="DeleteProductDiv ManagerForms">
      <form className="DeleteProductForm FormContent">
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>שם מוצר</label>
        <input type="text" className="input" />
      </form>
    </div>
  );
}
