import { useForm } from "react-hook-form";
import React, { useState, useEffect } from "react";
import { BrowserRouter as router } from "react-router-dom";

import "../css/ManagerForms.css";
import { ListBox } from "primereact/listbox";

import { SearchProductsByString } from "../FUNCTION/ProductFunction";
import Products from "./Product";

export default function DeleteProduct() {
  const [oneName, setoneName] = useState(" ");
  const [nameProduct, setnameProduct] = useState([" "]);

  // useEffect(() => {
  //   SearchProductsByString().then((res) => {
  //     console.log(res);
  //     for (var i = 0; i < res.length; i++) {
  //       const List = nameProduct;
  //       List[i] = res[i].NameProduct;
  //       setnameProduct(List);
  //     }
  //     setoneName(res[0].NameCategory);
  //     console.log(nameProduct);
  //   });
  // });

  function callSearch(val) {
    // const L = SearchProductsByString();
    // for (var i = 0; i < 2; i++) {
    //   const List = nameProduct;
    //   List[i] = L[i].nameProduct;
    //   nameProduct(List);
    // }
    // setoneName(L[0].NameCategory);
    // console.log({ nameProduct });
    //
    //
    SearchProductsByString().then((res) => {
      console.log(res);
      for (var i = 0; i < res.length; i++) {
        const List = nameProduct;
        List[i] = res[i].NameProduct;
        setnameProduct(List);
      }
      setoneName(res[0].NameCategory);
      console.log(nameProduct);
    });
  }

  return (
    <>
      <div className="AddProductDiv ManagerForms">
        <p className="explain">
          עמ"נ למחוק מוצר מן במערכת יש לפעול עפ"י הכללים הבאים:
        </p>
        <br />
        <ol type="1">
          <li>יש להקיש את שמו בתיבת הטקסט העליונה</li>
          <li>לאחר מכן יוצגו כל המוצרים המכילים את הטקטס אותו כתבת</li>
          <li>יש לבחור את המוצר המתבקש מתוך הרשימה</li>
          <li>לאחר מכן יש להקיש את סיסמת המנהל לאישור המחיקה</li>
        </ol>

        <div className="backgroundGradient">
          <form className="AddProductForm FormContent">
            <div className="row">
              <div className="col">
                <input
                  type="text"
                  className="form-control input"
                  autoFocus
                  placeholder=" "
                  val
                  onChange={(e) => callSearch(e.target.value)}
                ></input>

                <select type="select">
                  {nameProduct.map((item) => (
                    <option key={item}>{item}</option>
                  ))}
                </select>

                {/* {nameProduct.map((val)=>{
                  <option>{val}</option>
                })} */}

                {
                  (nameProduct.map = (val) => {
                    <ListBox
                      value={val}
                      options={val}
                      onChange={(n) => setnameProduct(n.value)}
                    />;
                  })
                }
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}
