/* Add Product Form */
function AddProduct() {
  /*onLoad*/
  const useMountEffect = (fun) => useEffect(fun, []);

  useMountEffect(() => {
    GetAllCategories();
  });

  /* on submit*/
  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  /* list Category*/
  const [myArray, setMyArray] = useState([]);

  /* list subCategory*/
  const [myArray2, setMyArray2] = useState([]);

  const [changeCategory, setCategory] = useState();
  const SelectCategory = (e) => {
    setCategory(e.target.value);
    GetSubCategories(e.target.value);
  };

  function GetAllCategories() {
    axios.get("Category/GetAllCategories").then((result) => {
      setMyArray(result.data);
      console.log(result.data);
    });
  }

  function GetSubCategories(selectedCategory) {
    debugger;

    // axios.get('Category/GetSubCategories/'+   function GetSubCategories(selectedCategory) {
    //   )
    // .then((response: any) => {
    //     console.log(response);
    //     this.setState({
    //         comments: response.data
    //     });

    axios
      .get("Category/GetSubCategories/" + selectedCategory)
      .then((result) => {
        setMyArray2(result.data);
        console.log(result.data);
      });

    // axios.get("Category/GetSubCategories", {
    //   params: {
    //     selectedCategory: selectedCategory,
    //   },
    // });
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <form
        className="AddProductForm FormContent"
        onSubmit={handleSubmit(onSubmit)}
      >
        <article>
          <label>שם מוצר</label>
          <input type="text" className="input" autoFocus />
          <label>קטגוריה</label>
          <br />
          <select type="select" options={myArray} onChange={SelectCategory}>
            {myArray.map((value) => (
              <option>{value}</option>
            ))}
          </select>
          <br />
          <label>תת קטגוריה</label>
          <br />
          <select type="select" options={myArray2}>
            {myArray2.map((value) => (
              <option>{value}</option>
            ))}
          </select>
          <br />
          <label>צבע</label>
          <input type="color" className="input" />
          <label>מידה</label>
          <input type="text" className="input" />
          <label>תיאור מוצר</label>
          <textarea
            rows="3"
            cols="34" /* value={this.state.value} onChange={(e) => this.setState({value: e.target.value})}*/
          ></textarea>
        </article>
        <article>
          <div label="DatePicker">
            <DatePicker />
          </div>
          <label>חברה</label>
          <input type="text" className="input" />
          <label>כמות</label>
          <input type="number" className="input" />
          <label>מחיר</label>
          <input type="money" className="input" />
          <label>תמונה</label>
          <input ref={register} type="file" name="picture" />
        </article>
      </form>
    </div>
  );
}
