import ReactDOM from "react-dom";
import React, { useState } from "react";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

import "../css/ManagerForms.css";

export default function ManagerForms(props) {
  const [color, setColor] = useState("pink");

  let colorStyle = {
    backgroundColor: { color },
  };

  function ChangeColor() {
    if (color == "pink") {
      setColor("green");
    } else {
      setColor("pink");
    }
  }

  return (
    <div>
      <Router>
        <table className="TablebtnForm">
          <tr>
            <ul>
              <li>
                <Link to="/AddProduct">הוספת מוצר</Link>
              </li>
              <li>
                <Link to="/shoppingCart">מחיקת מוצר</Link>
              </li>
              <li>
                <Link to="/shoppingCart">נתינת הרשאה</Link>
              </li>
              <li>
                <Link to="/shoppingCart">מחיקת הרשאה</Link>
              </li>
            </ul>
          </tr>
        </table>
        <div style={colorStyle.backgroundColor} className="main">
          <button className="ChangeColor" onClick={() => ChangeColor()}>
            ChangeColor
          </button>
        </div>
        <div className="BodyForms">
          <Switch>
            <Route to="ManagerForms">
              <AddProduct />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}

/* Add Product Form */
export function AddProduct() {
  return (
    <div className="AddProductDiv ManagerForms">
      <form className="AddProductForm">
        <label>שם מוצר</label>
        <input type="text" className="input" />
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>מחיר</label>
        <input type="money" className="input" />
      </form>
    </div>
  );
}

/* Delete Product Form */
export function DeleteProduct() {
  return (
    <div className="DeleteProductDiv ManagerForms">
      <form className="AddProductForm">
        <label>שם מוצר</label>
        <input type="text" className="input" />
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>מחיר</label>
        <input type="money" className="input" />
      </form>
    </div>
  );
}
