import React, { useState, useEffect } from "react";

import { useForm } from "react-hook-form";
import axios from "../axios";
// import Select from 'react-select';

import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

import "../css/ManagerForms.css";
import { DatePicker } from "antd";

function ManagerForms(props) {
  const [color, setColor] = useState("pink");

  let colorStyle = {
    backgroundColor: { color },
  };

  function ChangeColor() {
    if (color === "pink") {
      setColor("green");
    } else {
      setColor("pink");
    }
  }

  return (
    <div>
      <div style={colorStyle.backgroundColor} className="main">
        <button className="ChangeColor" onClick={() => ChangeColor()}>
          ChangeColor
        </button>
      </div>
      <Router>
        <div>
          <MenuForms />
          <Switch>
            <Route path="/ManagerForms/AddProduct">
              <AddProduct />
            </Route>
            <Route path="/ManagerForms/DeleteProduct">
              <DeleteProduct />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}
export default ManagerForms;

function MenuForms() {
  return (
    <div>
      <ul className="ListbtnForm">
        <li>
          <Link to="/ManagerForms/AddProduct" className="ListbtnForm">
            הוספת מוצר
          </Link>
        </li>
        <li>
          <Link to="/ManagerForms/DeleteProduct" className="ListbtnForm">
            מחיקת מוצר
          </Link>
        </li>
        <li>
          <Link to="/shoppingCart" className="ListbtnForm">
            נתינת הרשאה
          </Link>
        </li>
        <li>
          <Link to="/shoppingCart" className="ListbtnForm">
            מחיקת הרשאה
          </Link>
        </li>
      </ul>
    </div>
  );
}

/* Add Product Form */
function AddProduct() {
  /*onLoad*/
  const useMountEffect = (fun) => useEffect(fun, []);

  useMountEffect(() => {
    GetAllCategories();
  });

  /* on submit*/
  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  /* list Category*/
  const [myArray, setMyArray] = useState([]);

  /* list subCategory*/
  const [myArray2, setMyArray2] = useState([]);

  const [changeCategory, setCategory] = useState();
  const SelectCategory = (e) => {
    setCategory(e.target.value);
    GetSubCategories(e.target.value);
  };

  function GetAllCategories() {
    axios.get("Category/GetAllCategories").then((result) => {
      setMyArray(result.data);
      console.log(result.data);
    });
  }

  function GetSubCategories(selectedCategory) {
    debugger;

    // axios.get('Category/GetSubCategories/'+   function GetSubCategories(selectedCategory) {
    //   )
    // .then((response: any) => {
    //     console.log(response);
    //     this.setState({
    //         comments: response.data
    //     });

    axios
      .get("Category/GetSubCategories/" + selectedCategory)
      .then((result) => {
        setMyArray2(result.data);
        console.log(result.data);
      });

    // axios.get("Category/GetSubCategories", {
    //   params: {
    //     selectedCategory: selectedCategory,
    //   },
    // });
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <form
        className="AddProductForm FormContent"
        onSubmit={handleSubmit(onSubmit)}
      >
        <label>שם מוצר</label>
        <input type="text" className="input" />
        <label>קטגוריה</label>
        <select type="select" options={myArray} onChange={SelectCategory}>
          {myArray.map((value) => (
            <option>{value}</option>
          ))}
        </select>
        <input type="text" className="input" />

        <label>תת קטגוריה</label>
        <select type="select" options={myArray2}>
          {myArray2.map((value) => (
            <option>{value}</option>
          ))}
        </select>
        <br />
        <label>תיאור מוצר</label>
        <textarea
          rows="3"
          cols="34" /* value={this.state.value} onChange={(e) => this.setState({value: e.target.value})}*/
        ></textarea>
        <div label="DatePicker">
          <DatePicker />
        </div>

        <label>צבע</label>
        <input type="color" className="input" />
        <label>מידה</label>
        <input type="text" className="input" />
        <label>כמות</label>
        <input type="number" className="input" />
        <label>מחיר</label>
        <input type="money" className="input" />
        <input ref={register} type="file" name="picture" />
        <img src={register} alt="img" />
      </form>
    </div>
  );
}

/* Delete Product Form */
export function DeleteProduct() {
  return (
    <div className="DeleteProductDiv ManagerForms">
      <form className="DeleteProductForm FormContent">
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>שם מוצר</label>
        <input type="text" className="input" />
      </form>
    </div>
  );
}
