import React, { useState } from "react";

import { useForm } from "react-hook-form";
import axios from "../axios";
// import Select from 'react-select';

import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

import "../css/ManagerForms.css";
import { DatePicker } from "antd";

function ManagerForms(props) {
  const [color, setColor] = useState("pink");

  let colorStyle = {
    backgroundColor: { color },
  };

  function ChangeColor() {
    if (color === "pink") {
      setColor("green");
    } else {
      setColor("pink");
    }
  }

  return (
    <div>
      <div style={colorStyle.backgroundColor} className="main">
        <button className="ChangeColor" onClick={() => ChangeColor()}>
          ChangeColor
        </button>
      </div>
      <Router>
        <div>
          <MenuForms />
          <Switch>
            <Route path="/ManagerForms/AddProduct">
              <AddProduct />
            </Route>
            <Route path="/ManagerForms/DeleteProduct">
              <DeleteProduct />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}
export default ManagerForms;

function MenuForms() {
  return (
    <div>
      <ul className="ListbtnForm">
        <li>
          <Link to="/ManagerForms/AddProduct" className="ListbtnForm">
            הוספת מוצר
          </Link>
        </li>
        <li>
          <Link to="/ManagerForms/DeleteProduct" className="ListbtnForm">
            מחיקת מוצר
          </Link>
        </li>
        <li>
          <Link to="/shoppingCart" className="ListbtnForm">
            נתינת הרשאה
          </Link>
        </li>
        <li>
          <Link to="/shoppingCart" className="ListbtnForm">
            מחיקת הרשאה
          </Link>
        </li>
      </ul>
    </div>
  );
}

/* Add Product Form */
function AddProduct() {
  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  const [myArray, setMyArray] = useState([]);
  // const [inputValue, setInputValue] = useState();

  const GetAllCategories = () => {
    axios.get("Category/GetAllCategories").then((result) => {
      setMyArray(result.data);
      console.log(result.data);
    });
  };

  // function GetAllCategories() {
  //   axios.get("Category/GetAllCategories").then((result) => {
  //     setMyArray(result.data);
  //     console.log(result.data);
  //   });
  // }

  const [ValueCategory, setCategory] = useState(" ");
  const filterProducts = (e) => {
    console.log(e.target.value);
    alert(e.target.value);
  };
  function inc() {
    setCategory("inc");
  }

  return (
    <div className="AddProductDiv ManagerForms">
      <form
        className="AddProductForm FormContent"
        onSubmit={handleSubmit(onSubmit)}
      >
        <label>שם מוצר</label>
        <input type="text" className="input" />
        <label>קטגוריה</label>
        <select type="select" options={myArray} onChange={setCategory}>
          {myArray.map((value) => (
            <option>{value}</option>
          ))}
        </select>
        <p></p> />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>תיאור מוצר</label>
        <textarea
          rows="3"
          cols="34" /* value={this.state.value} onChange={(e) => this.setState({value: e.target.value})}*/
        ></textarea>
        <div label="DatePicker">
          <DatePicker />
        </div>
        <label>מחיר</label>
        <input type="money" className="input" />
        <input ref={register} type="file" name="picture" />
        <img src={register} alt="img" />
      </form>
    </div>
  );
}

/* Delete Product Form */
export function DeleteProduct() {
  return (
    <div className="DeleteProductDiv ManagerForms">
      <form className="DeleteProductForm FormContent">
        <label>קטגוריה</label>
        <input type="text" className="input" />
        <label>תת קטגוריה</label>
        <input type="text" className="input" />
        <label>שם מוצר</label>
        <input type="text" className="input" />
      </form>
    </div>
  );
}
